import React, { useEffect, useState } from 'react';
import axios from 'axios';

import { View, Text, TextInput, TouchableHighlight, StyleSheet, Image } from 'react-native';


export function Signup(props) {

    const [nombre, setNombre] = useState("");
    const [email, setEmail] = useState("");
   
    const [password1, setPassword1] = useState("");
    const [password2, setPassword2] = useState("");

    const role = ["user"]

   function signin(){
    axios.post('http://192.168.43.12:8080/api/auth/signup', {username: nombre, email: email,
    password: password,role: ["user"] })
    .then(res=>{
       alert(res.data.message)
    props.navigation.navigate("Map")})

    .catch(error=>{
        alert(res.data.message)
      })

   }


   function comprobar(){
   if(password1 === password2){
     signin();
   }else{
   alert("Las contrase単as no coinciden")
   }

   }
  
    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Image style={{ width: 100, height: 100, alignItems: 'center' }} source={require('../assets/def.png')} />
                <Text
                    style={{ color: '#333333', fontWeight: 'bold', fontFamily: 'Baskerville-Bold', fontSize: 30 }}>
                    Hi-Go!
                </Text>
            </View>


            <TextInput
                style={styles.input}
                placeholder="Username..."
                value={nombre}
                onChangeText={(text) => setNombre(text)}
            />

            <TextInput
                style={styles.input}
                placeholder="Email..."
                value={email}
                onChangeText={(text) => setEmail(text)}
            />
            <TextInput
                style={styles.input}
                placeholder="Contrase単a..."
                value={password1}
                secureTextEntry={true}
                onChangeText={(text) => setPassword1(text)}
            />
            <TextInput
                style={styles.input}
                placeholder=" Repetir Contrase単a..."
                value={password2}
                secureTextEntry={true}
                onChangeText={(text) => setPassword2(text)}
            />
            <TouchableHighlight style={styles.button} onPress={() => signin()}>
                <Text style={styles.textButton}>Sign up</Text>
            </TouchableHighlight>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#3EA9FA',
        // alignItems: 'center',
        justifyContent: 'space-around',
        padding: 15
    },
    header: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        alignItems: 'center',
    },
    input: {
        padding: 15,
        margin: 10,
        backgroundColor: 'white',
        color: 'black',
        borderWidth: 3,
        borderColor: 'black',
        fontSize: 25,
        fontWeight: 'bold',
        fontFamily: 'Times New Roman'
        // textAlign: 'center'
    },
    button: {
        alignSelf:'center',
        width: 242,
        height: 60,
        bottom: 15,
        borderRadius: 50,
        backgroundColor: '#333333',
        alignItems: 'center',
        justifyContent: 'center'
    },
    textButton: {
        color: '#FEFAE0',
        fontWeight: 'bold',
        fontFamily: 'AmericanTypewriter-Bold',
        fontSize: 40
    }
});
export default Signup;
